import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class HeadphoneSpecs extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			impedance: '',
			sensitivity: ''
		};

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		// Need for multiple events
		const target = event.target;
		const value = taget.name === 'impedance' ? target.checked : taget.value;
		const name = target.name;

		this.setState({
			[name]: value
		});
	}

	handleSubmit(event) {
		// Need for multiple submits
		e.preventDefault();
	}

	render() {
		return (
			<form onSubmit={this.handleSubmit}>
			  <label>
			    <h3> Headphone Impedance (Ohms): </h3>
			    <input 
			      name="impedance"
			      type="number" 
			      value={this.state.value}
			      onChange={this.handleChange}
			    />
			  </label>
			  <label>
			    <h3> Headphone Sensitivity (dB SPL / mW): </h3>
			      <input
			        name="sensitivity"
			        type="number"
			        value={this.state.value}
			        onChange={this.handleChange}
			      />
			  </label>
			  <input 
			    type="submit"
			    value="Submit"
			  />
			</form>
			);
	}
}
